select DISTINCT p.supplier_spu_id,p.supplier_spu_no,p.supplier_spu_model,p.product_url,p.create_time,p.supplier_id ,p.channel
from hub_supplier_spu p INNER JOIN hub_supplier_sku k ON p.supplier_spu_id = k.supplier_spu_id where k.sp_sku_no is not null

  and p.supplier_id = #{supplierId}


  and p.supplier_brandname = #{brandName}


  and p.channel = #{channel}

order by p.create_time desc

  limit #{pageIndex} , #{pageSize}

select count(DISTINCT p.supplier_spu_id,p.supplier_spu_no,p.supplier_spu_model,p.product_url,p.create_time,p.supplier_id ,p.channel)
from hub_supplier_spu p INNER JOIN hub_supplier_sku k ON p.supplier_spu_id = k.supplier_spu_id where k.sp_sku_no is not null

  and p.supplier_id = #{supplierId}


  and p.supplier_brandname = #{brandName}


  and p.channel = #{channel}
