mapper.update:
update ORDER_DETAIL
STATUS = #{status}, UPDATE_TIME = #{updateTime}, SP_PURCHASE_DETAIL_NO = #{spPurchaseDetailNo}, SP_PURCHASE_NO = #{spPurchaseNo}, DELIVERY_NO = #{deliveryNo}, EXC_STATE = #{excState}, EXC_DESC = #{excDesc}, PURCHASE_PRICE_DETAIL = #{purchasePriceDetail}, EXC_TIME = #{excTime} SUPPLIER_ORDER_NO = #{supplierOrderNo}
where ORDER_NO = #{orderNo}

mapper.select:
select
from ORDER_DETAIL
SUPPLIER_ID=#{supplierId} and SUPPLIER_NO=#{supplierNo} and STATUS=#{status}

select

from ORDER_DETAIL

	SUPPLIER_ID=#{supplierId}
	and  STATUS = #{status}
	and  EXC_STATE = #{excState}
	and CREATE_TIME >=DATE_SUB(#{date},INTERVAL  #{interval} HOUR)

select

from ORDER_DETAIL

	SUPPLIER_ID=#{supplierId}
	and  STATUS = #{status}

select

from ORDER_DETAIL

	SUPPLIER_ID=#{supplierId}
	and  STATUS = #{status}
	and CREATE_TIME >=DATE_SUB(#{date},INTERVAL  12 HOUR)

select

from ORDER_DETAIL

	SUPPLIER_ID=#{supplierId}
	and  STATUS = #{status}
	and CREATE_TIME >= #{startTime}
	
		 and CREATE_TIME < #{endTime}

select

from ORDER_DETAIL

	SUPPLIER_ID=#{supplierId}
	and  STATUS = #{status}
	and UPDATE_TIME >= #{startTime}
	
		 and UPDATE_TIME < #{endTime}

select

from ORDER_DETAIL ddd left join SUPPLIER su on ddd.SUPPLIER_ID = su.SUPPLIER_ID

	su.SUPPLIER_STATE = '1'
	and ddd.SUPPLIER_ID=#{supplierId}
	and CREATE_TIME >=#{startDate}
	
		 and CREATE_TIME <=#{endDate}    
	

order by SUPPLIER_NAME,CREATE_TIME ,STATUS

mapper.update:
update ORDER_DETAIL

	STATUS = #{status},
	UPDATE_TIME = #{updateTime}

where UUID = #{uuid}

update ORDER_DETAIL

	EXC_STATE = #{excState},
	EXC_DESC = #{excDesc},
	EXC_TIME = #{excTime}

where UUID = #{uuid}

update ORDER_DETAIL

	STATUS = #{status},
	UPDATE_TIME = #{updateTime},
	DELIVERY_NO = #{deliveryNo}

where UUID = #{uuid}

update ORDER_DETAIL
STATUS = #{status}, ORDER_NO = #{orderNo}, UPDATE_TIME = #{updateTime}, SP_PURCHASE_DETAIL_NO = #{spPurchaseDetailNo}, SP_PURCHASE_NO = #{spPurchaseNo}, DELIVERY_NO = #{deliveryNo}, EXC_STATE = #{excState}, EXC_DESC = #{excDesc}, PURCHASE_PRICE_DETAIL = #{purchasePriceDetail}, EXC_TIME = #{excTime}
where UUID = #{uuid}
