mapper.update:
update SPU
MATERIAL = #{material}, LAST_TIME = #{lastTime}, PRODUCT_ORIGIN = #{productOrigin}, CATEGORY_GENDER = #{categoryGender},
NEWSEASON_ID = #{seasonId}, NEWSEASON_NAME = #{seasonName}, CATEGORY_NAME = #{categoryName}
where SUPPLIER_ID = #{supplierId} and SPU_ID = #{spuId}

update SPU
SEASON_ID = #{seasonId}, SEASON_NAME = #{seasonName}
where SUPPLIER_ID = #{supplierId} and SPU_ID = #{spuId}

mapper.select:
select

from SPU


        SUPPLIER_ID = #{supplierId}


        and SPU_ID = #{spuId}

	   select
       		SEASON_NAME,MATERIAL,PRODUCT_ORIGIN
       from SPU


                SUPPLIER_ID = #{supplierId}


                and SPU_ID = #{spuId}

mapper.update:
update SPU
MEMO = CONCAT(IF(MEMO IS NULL ,"",MEMO)," ",#{memo}), LAST_TIME = #{lastTime},
where SUPPLIER_ID = #{supplierId} and SPU_ID = #{spuId}

update.foreach:
update SPU
MEMO = #{spu.memo}, LAST_TIME = NOW(),
where SUPPLIER_ID = #{spu.supplierId} and SPU_ID = #{spu.spuId}

mapper.update:
update SPU
SP_CATEGORY = #{spCategory}, SP_BRAND = #{spBrand},
where SUPPLIER_ID = #{supplierId} and SPU_ID = #{spuId}
