mapper.select:
select ID as ID, SUPPLIER_ID as SUPPLIER_ID, UUID as UUID, SP_ORDER_ID as SP_ORDER_ID, SP_PURCHASE_DETAIL_NO, SUPPLIER_ORDER_NO as SUPPLIER_ORDER_NO, STATUS as STATUS, DETAIL as DETAIL, SP_PURCHASE_NO as SP_PURCHASE_NO, UPDATE_TIME as UPDATE_TIME, CREATE_TIME as CREATE_TIME, EXC_STATE as EXC_STATE, EXC_DESC as EXC_DESC, PURCHASE_PRICE_DETAIL as PURCHASE_PRICE_DETAIL
from `ORDER`
SUPPLIER_ID=#{supplierId}
and STATUS = #{status}

select ID as ID, SUPPLIER_ID as SUPPLIER_ID, UUID as UUID, SP_ORDER_ID as SP_ORDER_ID, SP_PURCHASE_DETAIL_NO, SUPPLIER_ORDER_NO as SUPPLIER_ORDER_NO, STATUS as STATUS, DETAIL as DETAIL, SP_PURCHASE_NO as SP_PURCHASE_NO, UPDATE_TIME as UPDATE_TIME, CREATE_TIME as CREATE_TIME, EXC_STATE as EXC_STATE, EXC_DESC as EXC_DESC, PURCHASE_PRICE_DETAIL as PURCHASE_PRICE_DETAIL
from `ORDER`
SUPPLIER_ID=#{supplierId}
and STATUS = #{status}
and CREATE_TIME >=DATE_SUB(#{date},INTERVAL 12 HOUR)

select ID as ID, SUPPLIER_ID as SUPPLIER_ID, UUID as UUID, SP_ORDER_ID as SP_ORDER_ID, SP_PURCHASE_DETAIL_NO, SUPPLIER_ORDER_NO as SUPPLIER_ORDER_NO, STATUS as STATUS, DETAIL as DETAIL, SP_PURCHASE_NO as SP_PURCHASE_NO, UPDATE_TIME as UPDATE_TIME, CREATE_TIME as CREATE_TIME, EXC_STATE as EXC_STATE, EXC_DESC as EXC_DESC, PURCHASE_PRICE_DETAIL as PURCHASE_PRICE_DETAIL
from `ORDER`
SUPPLIER_ID=#{supplierId}
and STATUS = #{status}
and EXC_STATE = #{excState}
and CREATE_TIME >=DATE_SUB(#{date},INTERVAL #{interval} HOUR)

select ID as ID, SUPPLIER_ID as SUPPLIER_ID, UUID as UUID, SP_ORDER_ID as SP_ORDER_ID, SP_PURCHASE_DETAIL_NO, SUPPLIER_ORDER_NO as SUPPLIER_ORDER_NO, STATUS as STATUS, DETAIL as DETAIL, SP_PURCHASE_NO as SP_PURCHASE_NO, UPDATE_TIME as UPDATE_TIME, CREATE_TIME as CREATE_TIME, EXC_STATE as EXC_STATE, EXC_DESC as EXC_DESC, PURCHASE_PRICE_DETAIL as PURCHASE_PRICE_DETAIL
from `ORDER`
SUPPLIER_ID=#{supplierId}
and STATUS = #{status}
and CREATE_TIME >= #{startTime}
and CREATE_TIME < #{endTime}

select ID as ID, SUPPLIER_ID as SUPPLIER_ID, UUID as UUID, SP_ORDER_ID as SP_ORDER_ID, SP_PURCHASE_DETAIL_NO, SUPPLIER_ORDER_NO as SUPPLIER_ORDER_NO, STATUS as STATUS, DETAIL as DETAIL, SP_PURCHASE_NO as SP_PURCHASE_NO, UPDATE_TIME as UPDATE_TIME, CREATE_TIME as CREATE_TIME, EXC_STATE as EXC_STATE, EXC_DESC as EXC_DESC, PURCHASE_PRICE_DETAIL as PURCHASE_PRICE_DETAIL
from `ORDER`
SUPPLIER_ID=#{supplierId}
and STATUS = #{status}
and UPDATE_TIME >= #{startTime}
and UPDATE_TIME < #{endTime}

select ID as ID, SUPPLIER_ID , SUPPLIER_NO, UUID as UUID, SP_ORDER_ID , SP_PURCHASE_NO, SP_PURCHASE_DETAIL_NO, SUPPLIER_ORDER_NO as SUPPLIER_ORDER_NO, STATUS as STATUS, DETAIL as DETAIL, MEMO, UPDATE_TIME as UPDATE_TIME, CREATE_TIME as CREATE_TIME, EXC_STATE as EXC_STATE, EXC_DESC as EXC_DESC
from `ORDER`
SP_ORDER_ID=#{spOrderId}

select ID as ID, SUPPLIER_ID , SUPPLIER_NO, UUID, SP_ORDER_ID , SP_PURCHASE_NO, SP_PURCHASE_DETAIL_NO, SUPPLIER_ORDER_NO as SUPPLIER_ORDER_NO, STATUS , DETAIL, MEMO, UPDATE_TIME as UPDATE_TIME, CREATE_TIME as CREATE_TIME, EXC_STATE as EXC_STATE, EXC_DESC as EXC_DESC
from `ORDER`
UUID=#{uuId}

select ID as ID, SUPPLIER_ID , SUPPLIER_NO, UUID as UUID, SP_ORDER_ID , SP_PURCHASE_NO, SP_PURCHASE_DETAIL_NO, SUPPLIER_ORDER_NO as SUPPLIER_ORDER_NO, STATUS as STATUS, DETAIL as DETAIL, MEMO, UPDATE_TIME as UPDATE_TIME, CREATE_TIME as CREATE_TIME, EXC_STATE as EXC_STATE, EXC_DESC as EXC_DESC
from `ORDER`
SP_PURCHASE_NO=#{purchaseNo}

mapper.update:
update `ORDER`
STATUS = #{status}, MEMO = #{memo}, UPDATE_TIME = #{updateTime}, SUPPLIER_ORDER_NO = #{supplierOrderNo}, SP_PURCHASE_DETAIL_NO = #{spPurchaseDetailNo}, SP_PURCHASE_NO = #{spPurchaseNo}, DELIVERY_NO = #{deliveryNo}, EXC_STATE = #{excState}, EXC_DESC = #{excDesc},
PURCHASE_PRICE_DETAIL = #{purchasePriceDetail},
EXC_TIME = #{excTime}
where UUID = #{uuId}

update `ORDER`



     STATUS = #{status}, 
     UPDATE_TIME = #{updateTime}, 
     DELIVERY_NO = #{deliveryNo}



where UUID = #{uuId}

update `ORDER`

     EXC_STATE = #{excState}, 
     EXC_DESC = #{excDesc}, 
     EXC_TIME = #{excTime}



where UUID = #{uuId}

update `ORDER`
STATUS = #{status}, MEMO = #{memo}, UPDATE_TIME = #{updateTime}, SUPPLIER_ORDER_NO = #{supplierOrderNo}, DELIVERY_NO = #{deliveryNo}, EXC_STATE = #{excState}, EXC_DESC = #{excDesc}, EXC_TIME = #{excTime}
where UUID = #{uuId}

mapper.select:
select ID as ID, ddd.SUPPLIER_ID as SUPPLIER_ID, su.SUPPLIER_NAME as SUPPLIER_NAME, UUID as UUID, SP_ORDER_ID as SP_ORDER_ID, SP_PURCHASE_DETAIL_NO, SUPPLIER_ORDER_NO as SUPPLIER_ORDER_NO, STATUS as STATUS, DETAIL as DETAIL, MEMO as MEMO, SP_PURCHASE_NO as SP_PURCHASE_NO, UPDATE_TIME as UPDATE_TIME, CREATE_TIME as CREATE_TIME, EXC_STATE as EXC_STATE, EXC_DESC as EXC_DESC, PURCHASE_PRICE_DETAIL as PURCHASE_PRICE_DETAIL
from `ORDER` ddd left join SUPPLIER su on ddd.SUPPLIER_ID = su.SUPPLIER_ID
su.SUPPLIER_STATE = '1'
and ddd.SUPPLIER_ID=#{supplierId}
and CREATE_TIME >=#{startDate}
and CREATE_TIME <=#{endDate}
order by SUPPLIER_NAME,CREATE_TIME DESC,STATUS

select

from `ORDER` ddd


        ddd.SUPPLIER_ID=#{supplierId}


        and ddd.SP_ORDER_ID=#{orderId}
